.popup-modal {
	width: var(--popup-width, 500px);
	height: var(--popup-height, auto);
	margin: 20px;
}

.popup-slide {
	position: fixed;
	width: 350px;
	height: auto;
	transform: translateX(100%);
}

.popup-slide.position-top-right {
