	transform: translateY(0);
}

.popup-fullscreen {
	width: 100vw;
	height: 100vh;
	max-width: none;
	max-height: none;
	border-radius: 0;
	margin: 0;
}

/* Close button */
.popup-close {
	position: absolute;
	top: 10px;
	right: 10px;
	width: var(--popup-close-size);
	height: var(--popup-close-size);
	background: rgba(0, 0, 0, 0.1);
	border: none;
	border-radius: 50%;
	font-size: 20px;
	line-height: 1;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	transition: background-color 0.2s ease;
	z-index: 1;
}

.popup-close:hover {
	background: rgba(0, 0, 0, 0.2);
}

